import React, { useState } from 'react';
import styled from 'styled-components';
import { useTranslation } from 'react-i18next';
import { ReactTyped } from 'react-typed';
import ServiceCard from '../components/ServiceCard.js';
import StandardPopup from '../components/StandardPopup.js';
import FAQSection from '../components/FAQSection.js';
import PageContainer from '../components/PageContainer.js';
import Button from '../components/Button.js';
import { useNavigate } from 'react-router-dom';



const ServicesWrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  position: relative;
  margin: 0;
  padding: 0;
  overflow-x: hidden; // Prevenir scroll horizontal
`;

const ServicesContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(4, minmax(300px, 1fr));
  gap: 2rem;
  margin: 1.5rem auto;
  width: 95%;
  max-width: 1800px;
  justify-content: center;
  align-items: start;

  @media (max-width: 1800px) {
    grid-template-columns: repeat(2, minmax(300px, 1fr));
    max-width: 1200px;
  }

  @media (max-width: 968px) {
    grid-template-columns: 1fr;
    width: calc(100% - 2rem); // Asegura espacio para padding
    max-width: none;
    margin: 1rem auto;
    padding: 0;
    gap: 1rem;
  }
`;

const Title = styled.h1`
  text-align: center;
  color: ${props => props.theme.colors.secondaryBackground};
  margin: 2rem 0;
  font-size: 2.5rem;
  text-shadow: 0 0 10px rgba(0, 255, 255, 0.2);
  height: 3.5rem; // Add fixed height to reserve space
  display: flex;
  align-items: center;
  justify-content: center;

  @media (max-width: 768px) {
    font-size: 1.8rem;
    height: 2.8rem; // Adjust height for mobile
    margin: 1.5rem 0;
  }
`;

function Services() {
    const { t } = useTranslation();
    const navigate = useNavigate();
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [selectedService, setSelectedService] = useState(null);

    const services = ['landingPages', 'onlineStorePhotos', 'wordpress', 'optimization'];


    const handleContactClick = (serviceKey) => {
        setSelectedService(serviceKey);
        setIsPopupOpen(true);
    };

    const SERVICE_NAMES_ES = {
        landingPages: 'Landing Pages',
        wordpress: 'WordPress',
        optimization: 'Optimización Web',
        onlineStorePhotos: 'Tienda Oline + Fotos Profesionales'
    };



    const handleContact = (method) => {
        if (method === 'WHATSAPP') {
            // Obtenemos el nombre del servicio en español del objeto
            const serviceNameES = selectedService ? SERVICE_NAMES_ES[selectedService] : '';

            // Construimos el mensaje base
            let message = '¡Hola! ';

            // Agregamos la información del servicio si hay uno seleccionado
            if (selectedService) {
                message += `Me interesa obtener más información sobre el servicio de ${serviceNameES}.`;
            } else {
                message += 'Me gustaría obtener más información sobre sus servicios.';
            }

            const whatsappUrl = `https://wa.me/5491168805604?text=${encodeURIComponent(message)}`;
            window.open(whatsappUrl, '_blank');
        } else if (method === 'EMAIL') {
            setIsPopupOpen(false);
            navigate('/contact');

            setTimeout(() => {
                const calendarSection = document.querySelector('#calendar-section');
                if (calendarSection) {
                    calendarSection.scrollIntoView({ behavior: 'smooth' });
                }
            }, 100);
        }
        setIsPopupOpen(false);
    };

    return (
        <PageContainer>
            <ServicesWrapper>
                <Title>
                    <div style={{ minHeight: '1em' }}>
                        <ReactTyped
                            strings={[t('services.title')]}
                            typeSpeed={50}
                            showCursor={true}
                            cursorChar="|"
                        />
                    </div>
                </Title>
                <ServicesContainer>
                    {services.map((serviceKey) => (
                        <ServiceCard
                            key={serviceKey}
                            serviceKey={serviceKey}
                            onContactClick={handleContactClick}
                        />
                    ))}
                </ServicesContainer>

                <StandardPopup
                    isOpen={isPopupOpen}
                    onClose={() => setIsPopupOpen(false)}
                    titleKey="services.contactPopup.title"
                >
                    <Button
                        variant="primary"
                        size="small"
                        glow={true}
                        onClick={() => handleContact('WHATSAPP')}
                    >
                        {t('services.contactPopup.directContact')}
                    </Button>

                    <Button
                        variant="primary"
                        size="small"
                        glow={true}
                        onClick={() => handleContact('EMAIL')}
                    >
                        {t('services.contactPopup.schedule')}
                    </Button>
                </StandardPopup>

                <FAQSection />
            </ServicesWrapper>
        </PageContainer>
    );
}

export default Services;
