const Contact = () => {

  // Configuramos las animaciones del formulario
  const getFormAnimation = () => {
    // Retornar un objeto vacío para evitar cualquier animación
    return {};
  };


  const { t } = useTranslation();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    company: '',
    contactPreference: [],
    contactDays: [],
    contactTime: [],
    appointmentDate: '',
    appointmentTime: '10:00',
    appointmentMedium: '',
    observations: ''
  });

  const [submitStatus, setSubmitStatus] = useState({
    success: false,
    titleKey: '',
    message: ''
  });
  const [errors, setErrors] = useState({});
  const [selectedDate, setSelectedDate] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const [formStatus, setFormStatus] = useState({
    isSubmitting: false,
    isSuccess: false,
    error: null
  });
  const [isCaptchaVerified, setIsCaptchaVerified] = useState(false);

  // Nuevos estados para carga progresiva
  const [setShowTitle] = useState(false);
  const [isAppointmentLoading, setIsAppointmentLoading] = useState(true);



  const navigate = useNavigate();
  const formRef = useRef(null);

  useEffect(() => {
    // Mostrar título primero
    // setShowTitle(true);

    // Después de la animación del título, cargar el contenido
    const contentTimer = setTimeout(() => {
      setIsAppointmentLoading(false);
    }, 1000); // Este delay da tiempo para que el título termine su animación

    return () => {
      clearTimeout(contentTimer);
    };
  }, []);

  useEffect(() => {
    if (formStatus.isSuccess) {
      setSubmitStatus({
        success: true,
        titleKey: 'contact.form.status.success.title',
        message: t('contact.form.status.success.message')
      });
      setShowPopup(true);
    } else if (formStatus.error) {
      setSubmitStatus({
        success: false,
        titleKey: 'contact.form.status.error.title',
        message: t('contact.form.status.error.message')
      });
      setShowPopup(true);
    }
  }, [formStatus, t]);

  const validateForm = () => {
    console.log('Starting form validation');
    const newErrors = {};

    if (!formData.name.trim()) newErrors.name = true;
    if (!formData.email || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) newErrors.email = true;
    if (!formData.phone.trim()) newErrors.phone = true;
    if (!formData.observations || formData.observations.trim() === '') newErrors.observations = true;
    if (!isCaptchaVerified) newErrors.captcha = true;

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Función para manejar el envío del formulario
  // 3. Update handleSubmit function
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    console.log('URL API:', API_URL); // Debug

    try {
      const response = await fetch(`${API_URL}/api/contact`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      console.log('Response status:', response.status);
      console.log('Response headers:', Array.from(response.headers.entries()));

      const data = await response.json();
      console.log('Response data:', data);

      if (response.ok) {
        setFormStatus({ isSubmitting: false, isSuccess: true, error: null });
        handleReset();
      } else {
        throw new Error(data.message || 'Error en el servidor');
      }
    } catch (error) {
      console.error('Error detallado:', error); // Debug
      setFormStatus({
        isSubmitting: false,
        isSuccess: false,
        error: error.message
      });
    } finally {
      setIsLoading(false);
    }
  };

  // 4. Update handleReset function
  const handleReset = (e) => {
    if (e) {
      e.preventDefault();
    }

    setFormData({
      name: '',
      email: '',
      phone: '',
      company: '',
      contactPreference: [],
      contactDays: [],
      contactTime: [],
      appointmentDate: '',
      appointmentTime: '10:00',
      appointmentMedium: '',
      observations: ''
    });

    setSelectedDate(null);
    setErrors({});
    setIsCaptchaVerified(false);
    setSubmitStatus({
      success: false,
      titleKey: '',
      message: ''
    });
    setShowPopup(false);

    if (formRef.current) {
      formRef.current.reset();
      const radios = formRef.current.querySelectorAll('input[type="radio"]');
      radios.forEach(radio => {
        radio.checked = false;
      });

      const textarea = formRef.current.querySelector('textarea');
      if (textarea) {
        textarea.value = '';
      }

      const select = formRef.current.querySelector('select[name="appointmentTime"]');
      if (select) {
        select.value = '10:00';
      }
    }


    // Limpiar cualquier mensaje de error o éxito que pudiera estar mostrándose
    setShowPopup(false);
  };

  // Función para manejar la selección de fecha
  const handleDateSelect = (date) => {
    if (!date) return;
    const formattedDate = date.toISOString().split('T')[0];
    setSelectedDate(date);
    setFormData(prev => ({
      ...prev,
      appointmentDate: formattedDate
    }));
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    if (submitStatus.success) {
      setTimeout(() => navigate('/'), 500);
    }
  };

  console.log('Rendering popup with:', {
    isOpen: showPopup,
    titleKey: submitStatus.titleKey,
    message: submitStatus.message
  });

  return (
    <PageContainer>
