                                    value="zoom"
                                    checked={formData.appointmentMedium === 'zoom'}
                                    onChange={(e) => setFormData({
                                      ...formData,
                                      appointmentMedium: e.target.value
                                    })}
                                  />
                                  {t('contact.form.appointment.medium.options.zoom')}
                                </RadioLabel>
                                <RadioLabel>
                                  <input
                                    type="radio"
                                    name="appointmentMedium"
                                    value="meet"
                                    checked={formData.appointmentMedium === 'meet'}
                                    onChange={(e) => setFormData({
                                      ...formData,
                                      appointmentMedium: e.target.value
                                    })}
                                  />
                                  {t('contact.form.appointment.medium.options.meet')}
                                </RadioLabel>
                                <RadioLabel>
                                  <input
                                    type="radio"
                                    name="appointmentMedium"
                                    value="whatsapp"
                                    checked={formData.appointmentMedium === 'whatsapp'}
                                    onChange={(e) => setFormData({
                                      ...formData,
                                      appointmentMedium: e.target.value
                                    })}
                                  />
                                  {t('contact.form.appointment.medium.options.whatsapp')}
                                </RadioLabel>
                              </RadioContainer>
                            </PreferenceGroup>

                            <PreferenceGroup>
                              <CheckboxTitle $error={errors.captcha}>
                                {t('contact.captcha.title')}
                              </CheckboxTitle>
                              <div style={{ display: 'flex', justifyContent: 'center', width: '100%' }}> {/* Contenedor para centrado */}
                                <Captcha
                                  onVerify={(verified) => {
                                    setIsCaptchaVerified(verified);
                                    if (verified) {
                                      setErrors(prev => ({ ...prev, captcha: false }));
                                    } else {
                                      setErrors(prev => ({ ...prev, captcha: true }));
                                    }
                                  }}
                                />
                              </div>
                              {errors.captcha && (
                                <ErrorMessage>{t('contact.captcha.required')}</ErrorMessage>
                              )}
                            </PreferenceGroup>
                          </PreferencesContainer>
                        </FadeIn>
                      )}
                    </Suspense>
                  </Section>
                </FormGrid>

                <CustomButtonGroup>
                  <ClearButton
                    variant="secondary"
                    size="small"
                    onClick={handleReset}
                  >
                    {t('contact.form.buttons.clear')}
                  </ClearButton>
                  <SubmitButton
                    variant="primary"
                    size="small"
                    type="submit"
                  >
                    {t('contact.form.buttons.submit')}
                  </SubmitButton>
                  <SubmitButton
                    variant="secondary"
                    size="small"
                    type="button"
                    onClick={() => {
                      console.log('Testing API URL:', API_URL);
                      fetch(`${API_URL}/api/test-email`)
                        .then(res => res.json())
                        .then(data => console.log('Test response:', data))
                        .catch(err => console.error('Test error:', err));
                    }}
                  >
                    Test API
                  </SubmitButton>
                </CustomButtonGroup>
              </form>
            </FormContainer>
          </ContentFadeIn>
        </Suspense>
      </ContactWrapper>

      {/* Popup - carga diferida */}
      {showPopup && (
        <Suspense fallback={null}>
          <StandardPopup
            isOpen={showPopup}
            onClose={handleClosePopup}
            titleKey={submitStatus.titleKey}
          >
            <PopupMessage>
              <StatusIcon>
                {submitStatus.success ? "✅" : "❌"}
              </StatusIcon>
              <MessageText>
                {submitStatus.message}
              </MessageText>
            </PopupMessage>
          </StandardPopup>
        </Suspense>
      )}
    </PageContainer>
  );
}

export default Contact;
